import React, { Component } from 'react'
import { DndContext } from 'react-dnd'
import { HTML5Backend } from 'react-dnd-html5-backend'
import Sortly from 'react-sortly'

const ITEMS = [
  { id: 1, name: 'Wendi Gunawan Yusuf', path: [] },
  { id: 2, name: 'Ainur Rofiq', path: [] },
  { id: 3, name: 'Petit Rinetyo', path: [2] },
  { id: 4, name: 'Fauzan Azmi', path: [2, 3] },
  { id: 5, name: 'Netzerospace', path: [] },
];


const itemStyle = {
  border: '1px solid #ccc',
  cursor: 'move',
  padding: 10,
  marginBottom: 4,
};

const muteStyle = { opacity: 0.3 };

const ItemRenderer = (props) => {
  const {
    name, path, connectDragSource, connectDropTarget, 
    isDragging, isClosestDragging,
  } = props;
  const style = {
    ...itemStyle,
    ...(isDragging || isClosestDragging ? muteStyle : null),
    marginLeft: path.length * 30,
  };
  const el = <div style={style}>{name}</div>;
  return connectDragSource(connectDropTarget(el));
};

export default function Tree() {
  state = { items: ITEMS }
  
  handleChange = (items) => {
    this.setState({ items });
  }
	
  const { items } = this.state;
    return (
      <div>
        <Sortly 
          items={items} 
          itemRenderer={ItemRenderer}
          onChange={this.handleChange}
        />
      </div>
    );
}
